merge=function(x,m=mergelist){
    mergevals=unlist(m)
    sapply(x,function(y){
        if(y%in% mergevals) {
            i=which(sapply(m,function(mm)y%in%mm))
            return(names(m[i]))
        }else return(y)
    })
