    for(p in 2:length(poplist)) poplist2ul[poplist2[[p]]]=poplist2[[p]]
    data=databackup
    data$AC=matColSums(data$AC,poplist2ul)
    datasimple=data
    poplist=poplistsimple
}else{
    data=datasimple
    poplist=poplistsimple
}

